void oled_task_user(void) {
    if (is_keyboard_master()) {
        if (IS_LAYER_ON(RAISE)) {
            render_left2();
        }
        else if (IS_LAYER_ON(LOWER)) {
            render_left1();
        }
        else {
            render_left();
        }
    } else {
        if (IS_LAYER_ON(RAISE)) {
            render_right2();
        }
        else if (IS_LAYER_ON(LOWER)) {
            render_right1();
        }
        else {
            render_right();
        }
    }
}
